import { GET_ANIMAL, GET_ANIMALS } from "../types/AnimalTypes";



const initialState = {
    Animal:null, 
    Animals : null,

 } ; 


 const AnimalReducer = (state = initialState, action )=>{
     switch (action.type) {
         case GET_ANIMALS:
           return {
               ...state , 
               Animals : action.payload.GetAnimals
           }  ;

        case GET_ANIMAL : 
        return {
            ...state , 
            Animal : action.payload.GetAnimal
        };
      default:
          return state;
     }
 }



 export default AnimalReducer ;
