function PrivateRoute({ children }) {
  const token = localStorage.getItem("token");
  const user = useSelector((state) => state.Reducer.user);

  return token && user.role == "user" ? (
    children
  ) : (
    <Navigate to="/signUp"></Navigate>
  );
}
export default PrivateRoute;
