const { REGISTER, FAIL, GET_CURRENT, LOGIN } = require("../types/authTypes")



const initialState = {
user: null,
errors:null,
auth : false
}

 const authreducer = (state = initialState, { type, payload }) => {
    switch (type) {

    case REGISTER : 
    case LOGIN : 
    localStorage.setItem("token",payload.token)
        return {...state  , user : payload.user, auth: true,errors:null }

        case FAIL : 
        return {...state,errors:payload.errors  , auth:false}

       case GET_CURRENT : 
       return {...state , user : payload.user , auth: true} 

    default:
        return state ;
    }
}

export default authreducer
