import {FAIL, LOGIN, REGISTER} from "../types/authTypes"
import axios from "axios"
import { Navigate } from "react-router-dom";

export const register =(newuser, Navigate )=>async (dispatch)=>{

try {
    //register wala login method post 

    //..hkinaa maa lback 
    const res= await  axios.post ('/auth/signup', newuser)
    dispatch ({type : REGISTER, payload:res.data})  //  nrajaa3 f user msg o tokeen


} catch (error) {
    dispatch({type : FAIL , payload : error.res.data }) ; 
}

}; 


export const Login = (user)=> async (dispatch)=> {
    try {
        const res = await axios.post('/auth/signIn', user ) 
        dispatch ({type: LOGIN , payload : res.data }) ; 
        Navigate('/Profile') ; 
    } catch (error) {
        dispatch ({type : FAIL , payload : error.response.data})  
    }
}
