import React, { useState } from 'react';
import './Register.css'
import{ useDispatch} from 'react-redux'
import { register } from '../redux/actions/authAction';
import {Link, useNavigate} from 'react-router-dom'


function Register() {


const [ email , setemail]= useState();
const [ password , setpassword] = useState();

const [username , setusername]=useState();

const [Date , setDate] =useState();

const Navigate =useNavigate()

const dispatch = useDispatch()

  return (

  <div >
                              <div> <title>Registration Form</title>
                    <div className="registration-form" >
                    <h1> Sign up
                    <form  >


{/*               
<p>Full Name:</p>
 <input type="text" name="fullname" placeholder="Full Name" /> */}


 <p>User Name:</p> 
  <input 
  value={username} 
 onChange={(e)=>setusername(e.target.value)}

 type="text" name="username" placeholder="User Name" />    

              <p>Email:</p> 
              <input
                 value={email}
                 onChange={(e)=> setemail(e.target.value)}
              type="email" name="email" placeholder="Email" />

              <p>Password:</p>
              <input 
                value={password}
                onChange={(e)=> setpassword(e.target.value)}
              type="password" name="password" placeholder="Password" />

<p>Date of birth:</p>
<input 
value={Date}
onChange={(e)=>setDate (e.target.value) }
type="birth" name="birth" placeholder="Date" /> 

                   <button className='button'
                    onClick={(e)=>{e.preventDefault(); 
                  dispatch(register({ email,password,username,birthDate:Date} , Navigate ))

                  }} 
                  > Sign Up
                   </button>
                       <div className='account?' >
          <p>
  already have an account ?
    <span  className='sign-up-now' >
         {" "}

   <Link  to='/signup' >

    <a href="#"> Sign In</a> {" "}
    </Link>
    </span>
         </p>
</div>
 </form> 
 </h1>
 </div> 
  </div> 




  <div  className="login"  >
        <title>Registration Form</title>

        <div className="registration-form" >
          <h1> Sign In
            <form action="#" method="post">

  <p>Email:</p>  

              <input
           value={email}
           onChange={(e)=>  setemail(e.target.value)}
              type="email" name="email" placeholder="Email" />
              <p>Password:</p>

              <input 
       value={password}
       onChange={(e)=> setpassword (e.target.value)}

              type="password" name="password" placeholder="Password" />


               <button className='button'
                    onClick={(e)=>{e.preventDefault(); 
                  dispatch(login ({ email,password } , Navigate ))   
                  }} 
                  > Sign In </button>

<div className='account?' >

  <p>

    <span  className='sign-up-now' >
{" "}

Don't have an account !

<Link  to='/signup' >

 <a href="#"> Sign Up</a> {" "}
  </Link>
    </span>
  </p>

</div>
            </form> 
          </h1>
          </div> 
  </div> 


























  </div>


  ) 
  }
export default Register ;
